import {createSlice} from "@reduxjs/toolkit"
const blogSlice = createSlice({
    name:'blog',
    initialState:{
userInfo:localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):null,
requestUser:false,
errorUser:'',
loadRequest:false,
errorRequest:''
    },
    reducers:{
    login:(state,action) =>{
        localStorage.setItem('userInfo',JSON.stringify(action.payload.userInfo))
        return {...state,userInfo:action.payload.userInfo}
    },
    requestSignin:(state,action)=>({...state,requestUser:action.payload.requestUser}),
    errorSignin:(state,action)=>({...state,errorUser:action.payload.errorUser}),
    registerLoad:(state,action)=>({...state,requestUser:action.payload.requestUser}),
    registerError:(state,action)=>({...state,errorUser:action.payload.errorUser})

    }
})
export const {login,requestSignin,errorSignin,registerLoad,registerError} = blogSlice.actions
export default blogSlice.reducer
