${globalStyle};
display:grid;
grid-template-columns:1fr

`
export const Label = styled.label`
align-self:center;
font-size:18px

`
export const Input = styled.input`
height:50px;
border:none;
border-bottom:1px solid rgba(0,0,0,.25);
font-size:18px;
outline:none;
&:focus{
    border-bottom:1px solid teal;
};
transition:all 0.1s ease-in-out

`
export const Button = styled.button`
${globalStyle};
height:45px;
width:120px;
background:teal;
border:none;
margin:0 auto;
border-radius:5px;
color:#fff;
font-size:18px;
cursor:pointer
