import React,{useState} from "react"
import {Select,Option} from "../styles/postStyle"
import { Checked } from "../styles/blogg"
 const Cate = ({cat,choose,categories}) =>{
    let clicked = false
    const addCate = () =>{
clicked = !clicked
      console.log(clicked)
    }
    return(<>
{choose &&<Select  clicked = {clicked}  >
    <Option onClick = {addCate}>{cat.charAt(0).toUpperCase() + cat.slice(1)} {clicked   ?<Checked />:null}</Option>
</Select>}
</>
    )
}
export default Cate
