import React,{useState,useEffect} from "react"
import { useSelector } from "react-redux"
import {Select,Option,SelectItem,Header} from "./styles/postStyle"
import {BlogItem,ItemImg,EditTitle,Head,User,Dates,EditDesc,Button} from "./styles/blogg"
import { BlogContainer } from "../components/sidebar/styles/sidebarstyle"
import Sidebar from "../components/sidebar/sidebar"
import axios from "axios"
const Edit = () =>{
    const blog = useSelector(state=>state.blog.blog)
    const [title,setTitle] = useState(blog.title?blog.title:"")
    const [description,setDescription] = useState(blog.description?blog.description:"")
    const [clicked,setClicked] = useState(false)
    const [choose,setChoose] = useState(false)
const categories= useState([])
const addCateS = () =>{
    const index = categories.findIndex(x=>x === "sports")
    setClicked(!clicked)
    if(clicked)
    categories.push('sports')
    else categories.splice(1,index)
}
const addCateL = () =>{
    const index = categories.findIndex(x=>x === "life")
    setClicked(!clicked)
    if(clicked)
    categories.push('life')
    else categories.splice(1,index)
}
const addCateSt = () =>{
    const index = categories.findIndex(x=>x === "style")
    setClicked(!clicked)
    if(clicked)
    categories.push('style')
    else categories.splice(1,index)
}
const addCateT = () =>{
    const index = categories.findIndex(x=>x === "tech")
    setClicked(!clicked)
    if(clicked)
    categories.push('tech')
    else categories.splice(1,index)
}
const addCateM= () =>{
    const index = categories.findIndex(x=>x === "music")
    setClicked(!clicked)
    if(clicked)
    categories.push('music')
    else categories.splice(1,index)
}
const addCateC = () =>{
    const index = categories.findIndex(x=>x === "cinema")
    setClicked(!clicked)
    if(clicked)
    categories.push('cinema')
    else categories.splice(1,index)
}
const addCateE = () =>{
    const index = categories.findIndex(x=>x === "entertainment")
    setClicked(!clicked)
    if(clicked)
    categories.push('entertainment')
    else categories.splice(1,index)
}
    return(
        <BlogContainer>
        <BlogItem>
            <ItemImg src = {blog.photo} />
            <EditTitle placeholder = "Enter title" value = {title} onChange = {(e)=>setTitle(e.target.value)} />
            <Head two>
                <User>
                    Author: {blog.username}
                </User>
                <Dates>{new Date(blog.createdAt).toDateString()}</Dates>
            </Head>
            <EditDesc placeholder = "Enter description" value = {description} onChange = {(e)=>setDescription(e.target.value)} />
            <SelectItem two>
                <Header onClick = {()=>setChoose(!choose)}>Choose Category: </Header>
            <Select choose = {choose} onClick = {()=>setChoose(true)}>
                <Option clicked = {clicked} onClick = {addCateS}>Sports</Option>
                {choose?<><Option clicked = {clicked} onClick = {addCateL}>Life</Option>
                <Option clicked = {clicked} onClick = {addCateT}>Tech</Option>
                    <Option clicked = {clicked} onClick = {addCateSt}>Style</Option>
                <Option clicked = {clicked} onClick = {addCateM} >Music</Option>
                <Option clicked = {clicked} onClick = {addCateC}>Cinema</Option>
                <Option clicked = {clicked} onClick = {addCateE} >Entertainment</Option>
                </>:null}
            </Select>
            </SelectItem>
            <Button>Update</Button>
        </BlogItem>
        <Sidebar />
        </BlogContainer>
    )
}
export default Edit
