import React,{useState} from "react"
import {Select,Option} from "../styles/postStyle"
import { Checked } from "../styles/blogg"
 const Cate = ({cat,choose,categories}) =>{
    const [clicked,setClicked] = useState(false)
    const addCate = () =>{
        setClicked(!clicked)
       if(clicked === true) categories.push(cat)
    categories.filter(x=>x ===  cat && clicked === false)
        console.log(categories)
    }
    
    return(<>
{choose &&<Select  clicked = {clicked} onClick = {addCate}>
    <Option >{cat.charAt(0).toUpperCase() + cat.slice(1)} {clicked  ?<Checked />:null}</Option>
</Select>}
</>
    )
}
export default Cate
