import React,{useState,useEffect} from "react"
import Sidebar from "../components/sidebar/sidebar"
import { useParams } from "react-router-dom"
import { useSelector,useDispatch } from "react-redux"
import { listPosts } from "./actions/postactions"
import Post from "../components/posts/post"
import { BlogContainer } from "../components/sidebar/styles/sidebarstyle"
import { PostC } from "../components/posts/styles/poststyle"
const SearchScreen = () =>{
    const {title = "all",description = "all",category= "all",user= "all"} = useParams()
    const dispatch = useDispatch()
    const result = useSelector(state=>state.blog.result)
    useEffect(()=>{
dispatch(listPosts({
    title:title !== "all" ?title:"",
    description:description !== "all"?description:"",
    category:category !== "all"?category:"",
    user:user !== "all"?user:""
}))
    },[title,category,description,user])
    const getFilterUrl = (filter) =>{
        const filterTitle = filter.title || title
        const filterCate = filter.category || category
        const filterUser = filter.user || user
        const filterDesc = filter.description || description
        return `/search/title/${filterTitle}/description/${filterDesc}/category/${filterCate}/user/${filterUser}`
    }
    console.log(result)
    return(
        <BlogContainer>
<PostC>
    {result.map(item=>(
   <Post item = {item} />
    ))}
</PostC>
<Sidebar />
    </BlogContainer>
    )
}
export default SearchScreen
