import axios from "axios"
import  {login,requestSignin,errorSignin} from "../../utils/store"
export const signin = ({email,password}) =>async(dispatch)=>{
dispatch(requestSignin({requestUser:true}))
try{
    const {data} = await axios.post('/api/users/signin',{email,password})
    dispatch(login({userInfo:data}))
    dispatch(requestSignin({requestUser:false}))

}
catch(error){
    dispatch(requestSignin({requestUser:false}))
  dispatch(errorSignin({errorUser:error.message && error.data.response.message ?error.data.response.message :error.message}))
}
}
