import React,{useState} from "react"
import {Select,Option} from "../styles/postStyle"
import { Checked } from "../styles/blogg"
 const Cate = ({cat,choose,categories}) =>{
    let clicked = false
    const addCate = () =>{
clicked = !clicked
const index = categories.findIndex(x=>x === cat )
if(clicked)
    categories.splice(index,0,cat)

else
    categories.splice(index,1)

   console.log(clicked)
   console.log(categories)
}
    return(<>
{choose &&<Select  clicked = {clicked === true}  onClick = {addCate}>
    <Option >{cat.charAt(0).toUpperCase() + cat.slice(1)} {clicked   ?<Checked />:null}</Option>
</Select>}
</>
    )
}
export default Cate
